defmodule PollWeb.IconComponent do
  use Phoenix.Component
  import Phoenix.HTML

  @doc """
  Renders an SVG icon. Pass the `name` of the icon, `class` for styling, and additional assigns as needed.
  """
  def render(assigns) do
    assigns =
      Enum.reduce(
        [:class, :name, :viewBox, :width, :height, :fill, :stroke, :aria_label],
        assigns,
        fn attr, acc ->
          assign_new(acc, attr, fn ->
            default_value(attr)
          end)
        end
      )

    ~H"""
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox={@viewBox}
      width={@width}
      height={@height}
      fill={@fill}
      stroke={@stroke}
      class={@class}
      aria-hidden={@aria_label == nil}
      aria-label={@aria_label}
    >
      <%= raw(get_icon_path(@name)) %>
    </svg>
    """
  end

  defp default_value(:class), do: ""
  defp default_value(:name), do: "default_icon"
  defp default_value(:viewBox), do: "0 0 24 24"
  defp default_value(:width), do: "24"
  defp default_value(:height), do: "24"
  defp default_value(:fill), do: "currentColor"
  defp default_value(:stroke), do: "currentColor"
  defp default_value(:aria_label), do: nil

